import { createSlice } from "@reduxjs/toolkit";


const cartSlice = createSlice({
    name: "cart",
    initialState: { 
        items: [],
        totalQuantity: 0
     },
    reducers: {
        increaseCartItem(state) {
            state.totalQuantity++;
        },
        decreaseCartItem(state) {
            state.totalQuantity--;
        }
    }
})

export const cartActions = cartSlice.actions;
export default cartSlice.reducer;
